mapper.select:
SELECT FROM( SELECT rownum rownu,t1.* FROM( SELECT * FROM JOB_QRTZ_TRIGGER_INFO
AND job_group = #{jobGroup}
AND job_desc like CONCAT(CONCAT('%', #{jobDesc}), '%')
AND executor_handler like CONCAT(CONCAT('%', #{executorHandler}), '%')
ORDER BY id DESC) t1 WHERE rownum <= (#{offset} + #{pagesize}) ) t WHERE rownu > #{offset}

SELECT 
FROM JOB_QRTZ_TRIGGER_INFO  t
WHERE t.id = #{id}

SELECT 
FROM JOB_QRTZ_TRIGGER_INFO t
WHERE t.job_group = #{jobGroup}
