mapper.select:
SELECT 
FROM(
SELECT rownum rownu,t1.* FROM(
SELECT * FROM JOB_QRTZ_TRIGGER_LOG


		AND job_group = #{jobGroup}


		AND job_id = #{jobId}


		AND trigger_time  >=  #{triggerTimeStart}


		AND trigger_time  <=  #{triggerTimeEnd}


		AND handle_code = 200


		AND (
			(trigger_code  >  0 AND trigger_code!=200) OR
			(handle_code  >  0 AND handle_code!=200)
		)


		AND (trigger_code = 200 AND handle_code=0)


ORDER BY id DESC) t1 WHERE rownum  <=  (#{offset} + #{pagesize}) ) t WHERE rownu  >  #{offset}

SELECT count(1)
FROM JOB_QRTZ_TRIGGER_LOG  t


		AND t.job_group = #{jobGroup}


		AND t.job_id = #{jobId}


		AND t.trigger_time  >=  #{triggerTimeStart}


		AND t.trigger_time  <=  #{triggerTimeEnd}


		AND t.handle_code = 200


		AND (
		(t.trigger_code  >  0 AND t.trigger_code!=200) OR
		(t.handle_code  >  0 AND t.handle_code!=200)
		)


		AND (t.trigger_code = 200 AND t.handle_code=0)

SELECT 
FROM JOB_QRTZ_TRIGGER_LOG  t
WHERE t.id = #{id}

mapper.insert:

	select SEQ_JOB_LOG.NEXTVAL from dual  

INSERT INTO JOB_QRTZ_TRIGGER_LOG (
	id,
	job_group,
	job_id
) VALUES (
	#{id},
	#{jobGroup},
	#{jobId}
)

mapper.update:
UPDATE JOB_QRTZ_TRIGGER_LOG
SET 
	handle_time= #{handleTime}, 
	handle_code= #{handleCode},
	handle_msg= #{handleMsg} 
WHERE id= #{id}

mapper.select:
SELECT count(1)
FROM JOB_QRTZ_TRIGGER_LOG  t


		AND t.handle_code = #{handleCode}

        SELECT TO_CHAR(trigger_time,'yyyy-MM-dd') "triggerDay", COUNT(id) "triggerCount"
        FROM JOB_QRTZ_TRIGGER_LOG
        WHERE trigger_time BETWEEN #{from} and #{to}

			AND handle_code = #{handleCode}

        GROUP BY TO_CHAR(trigger_time,'yyyy-MM-dd')

mapper.delete:
delete from JOB_QRTZ_TRIGGER_LOG


		AND job_group = #{jobGroup}


		AND job_id = #{jobId}


		AND trigger_time  <=  #{clearBeforeTime}


		AND id NOT in(
			SELECT id FROM(
				SELECT rownum rownu,id from 
					(SELECT id FROM JOB_QRTZ_TRIGGER_LOG t


							AND t.job_group = #{jobGroup}


							AND t.job_id = #{jobId}


					ORDER BY t.trigger_time desc)
				where rownum  <=  #{clearBeforeNum}
			) 
		)
