mapper.select:
SELECT * FROM (
	SELECT ROWNUM rn, T1.* FROM (
		SELECT 
		FROM xxl_job_info t

		ORDER BY id DESC
	) T1
	WHERE ROWNUM <= #{end}
) T2
WHERE T2.rn >= #{offset}

SELECT 
FROM xxl_job_info t
WHERE t.id = #{id}

SELECT 
FROM xxl_job_info t
WHERE t.job_group = #{jobGroup}

SELECT T1.* FROM (
	SELECT 
	FROM xxl_job_info t
	WHERE t.trigger_status = 1
		and t.trigger_next_time  <=  #{maxNextTime}
	ORDER BY id ASC
) T1
WHERE ROWNUM  <=  #{pagesize}

mapper.update:
UPDATE xxl_job_info
SET
	trigger_last_time = #{triggerLastTime},
	trigger_next_time = #{triggerNextTime},
	trigger_status = #{triggerStatus}
WHERE id = #{id}
