mapper.select:
SELECT count(*) FROM xxl_job_info AS t
AND t.job_group = #{jobGroup}
AND t.trigger_status = #{triggerStatus}
AND t.job_desc like CONCAT(CONCAT('%', #{jobDesc}), '%')
AND t.executor_handler like CONCAT(CONCAT('%', #{executorHandler}), '%')
AND t.author like CONCAT(CONCAT('%', #{author}), '%')

SELECT 
FROM xxl_job_info AS t
WHERE t.id = #{id}

mapper.update:
UPDATE xxl_job_info
trigger_status = #{triggerStatus}, trigger_last_time = #{triggerLastTime}, trigger_next_time = #{triggerNextTime}, executor_timeout = ${executorTimeout}, executor_fail_retry_count = ${executorFailRetryCount}, job_group = #{jobGroup}, job_cron = #{jobCron}, job_desc = #{jobDesc}, child_jobid = #{childJobId}, glue_type = #{glueType}, glue_source = #{glueSource}, glue_remark = #{glueRemark}, glue_updatetime = #{glueUpdatetime}, executor_param = #{executorParam}, executor_handler = #{executorHandler}, executor_route_strategy = #{executorRouteStrategy}, executor_block_strategy = #{executorBlockStrategy}, alarm_email = #{alarmEmail}, author = #{author},
update_time = NOW() update_time = #{updateTime }
WHERE id = #{id}

mapper.select:
SELECT 
FROM xxl_job_info AS t
WHERE t.job_group = #{jobGroup}

SELECT 
FROM xxl_job_info AS t
WHERE t.trigger_status = 1
and t.trigger_next_time  <=  #{maxNextTime}
ORDER BY id ASC
LIMIT #{pagesize}

mapper.update:
UPDATE xxl_job_info
SET
	trigger_last_time = #{triggerLastTime},
	trigger_next_time = #{triggerNextTime},
	trigger_status = #{triggerStatus}
WHERE id = #{id}
