mapper.select:
SELECT 
FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t

	
		AND t.job_group = #{jobGroup}
	
	
		AND t.job_id = #{jobId}
	
	
		AND t.trigger_time  >=  #{triggerTimeStart}
	
	
		AND t.trigger_time  <=  #{triggerTimeEnd}
	

ORDER BY id DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1)
FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t

	
		AND t.job_group = #{jobGroup}
	
	
		AND t.job_id = #{jobId}
	
	
		AND t.trigger_time  >=  #{triggerTimeStart}
	
	
		AND t.trigger_time  <=  #{triggerTimeEnd}

SELECT 
FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t
WHERE t.id = #{id}

mapper.insert:
INSERT INTO XXL_JOB_QRTZ_TRIGGER_LOG (
	`job_group`,
	`job_id`
) VALUES (
	#{jobGroup},
	#{jobId}
);
 
	SELECT LAST_INSERT_ID()

mapper.select:
SELECT count(1)
FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t

	
		AND t.handle_code = #{handleCode}

        SELECT DATE_FORMAT(trigger_time,'%Y-%m-%d') triggerDay, COUNT(id) triggerCount
        FROM XXL_JOB_QRTZ_TRIGGER_LOG
        WHERE trigger_time BETWEEN #{from} and #{to}
		
			AND handle_code = #{handleCode}
		
        GROUP BY triggerDay;

mapper.delete:
delete from XXL_JOB_QRTZ_TRIGGER_LOG

	
		AND job_group = #{jobGroup}
	
	
		AND job_id = #{jobId}
	
	
		AND trigger_time  <=  #{clearBeforeTime}
	
	
		AND id NOT in(
			SELECT id FROM(
				SELECT id FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t
				
					
						AND t.job_group = #{jobGroup}
					
					
						AND t.job_id = #{jobId}
					
				
				ORDER BY t.trigger_time desc
				LIMIT 0, #{clearBeforeNum}
			) t1
		)
