mapper.update:
        UPDATE XXL_JOB_QRTZ_TRIGGER_REGISTRY

        	SET update_time = NOW()


			SET update_time = (select NOW())

        WHERE registry_group = #{registryGroup}
          AND registry_key = #{registryKey}
          AND registry_value = #{registryValue}

mapper.delete:
DELETE FROM XXL_JOB_QRTZ_TRIGGER_REGISTRY
WHERE registry_group = #{registryGroup}
	AND registry_key = #{registryKey}
	AND registry_value = #{registryValue}
