mapper.sql:
FROM XXL_JOB_INFO t LEFT JOIN ( SELECT "JOB_ID","TRIGGER_TIME" AS "NEWEST_TRIGGER_TIME", CASE WHEN "HANDLE_CODE" =200 THEN 1 WHEN "TRIGGER_CODE" NOT IN (0,200) OR "HANDLE_CODE" NOT IN (0,200) THEN 2 WHEN "TRIGGER_CODE" =200 AND "HANDLE_CODE" =0 THEN 3 ELSE -1 END AS "NEWEST_LOG_STATUS" FROM "XXL_JOB_LOG" WHERE "ID" IN ( SELECT MAX("ID") AS MAX_ID FROM "XXL_JOB_LOG" b GROUP BY "JOB_ID" ) ) b ON t."ID"=b."JOB_ID"
AND t."JOB_GROUP" = #{jobGroup,jdbcType=NUMERIC}
AND t."TRIGGER_STATUS" = #{triggerStatus,jdbcType=NUMERIC}
AND t."JOB_DESC" like CONCAT(CONCAT('%', #{jobDesc,jdbcType=VARCHAR}), '%')
AND t."EXECUTOR_HANDLER" like CONCAT(CONCAT('%', #{executorHandler,jdbcType=VARCHAR}), '%')
AND t."AUTHOR" like CONCAT(CONCAT('%', #{author,jdbcType=VARCHAR}), '%')

mapper.select:
