SELECT TMP.*,ROWNUM
FROM (

SELECT

FROM XXL_JOB_INFO t
WHERE t."TRIGGER_STATUS" = 1
and t."TRIGGER_NEXT_TIME" <= #{maxNextTime,jdbcType=NUMERIC}
ORDER BY "ID" ASC

) TMP
where ROWNUM <= #{pagesize,jdbcType=NUMERIC}
