mapper.sql:
FROM XXL_JOB_LOG t
left join XXL_JOB_INFO f on t."JOB_ID"=f."ID"
left join XXL_JOB_GROUP g on t."JOB_GROUP" = g."ID"


        AND t."JOB_GROUP" = #{jobGroup,jdbcType=NUMERIC}


        AND t."JOB_ID" = #{jobId,jdbcType=NUMERIC}


        AND t."TRIGGER_TIME" >= #{triggerTimeStart,jdbcType=DATE}


        AND t."TRIGGER_TIME" <= #{triggerTimeEnd,jdbcType=DATE}


        AND t."HANDLE_CODE" = 200


        AND (
        t."TRIGGER_CODE" NOT IN (0, 200) OR
        t."HANDLE_CODE" NOT IN (0, 200)
        )


        AND t."TRIGGER_CODE" = 200
        AND t."HANDLE_CODE" = 0

mapper.select:
SELECT *
FROM (SELECT TMP.*, ROWNUM ROW_ID
FROM (

SELECT


ORDER BY t."TRIGGER_TIME" DESC


) TMP
WHERE ROWNUM <= #{pagesize,jdbcType=NUMERIC}
) TMP
WHERE ROW_ID > #{offset,jdbcType=NUMERIC}

SELECT

FROM XXL_JOB_LOG t
WHERE t."ID" = #{id,jdbcType=NUMERIC}

select "ID" from (

SELECT "ID" FROM XXL_JOB_LOG


        AND "JOB_GROUP" = #{jobGroup,jdbcType=NUMERIC}


        AND "JOB_ID" = #{jobId,jdbcType=NUMERIC}


        AND "TRIGGER_TIME" <= #{clearBeforeTime,jdbcType=DATE}


        AND "ID" NOT in(
        SELECT "ID" FROM(

        select "ID" from (
        SELECT "ID" FROM XXL_JOB_LOG t



                AND t."JOB_GROUP" = #{jobGroup,jdbcType=NUMERIC}


                AND t."JOB_ID" = #{jobId,jdbcType=NUMERIC}


        ORDER BY t."TRIGGER_TIME" desc
        ) p
        where p.ROWNUM <= #{clearBeforeNum,jdbcType=NUMERIC}
        ) t1
        )


order by "ID" asc
) tmp
where ROWNUM <= #{pagesize,jdbcType=NUMERIC}
